import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [storyboards, setStoryboards] = useState([]);
  const [currentStoryboard, setCurrentStoryboard] = useState({});
  const [selectedChoice, setSelectedChoice] = useState('');

  useEffect(() => {
    axios.get('/api/storyboards')
      .then(res => {
        setStoryboards(res.data);
        setCurrentStoryboard(res.data[0]); // set the first storyboard as the initial one
      })
      .catch(err => console.log(err));
  }, []);

  const handleChoiceSelect = (choice) => {
    setSelectedChoice(choice);
    const nextStoryboardId = currentStoryboard.choices[choice].next;
    setCurrentStoryboard(storyboards.find(s => s.id === nextStoryboardId));
  };

  const handleSave = () => {
    axios.post('/api/storyboards', currentStoryboard)
      .then(res => console.log(res))
      .catch(err => console.log(err));
  };

  const handleLoad = (storyboard) => {
    setCurrentStoryboard(storyboard);
  };

  return (
    <div className="App">
      <h1>Story Adventure Web App</h1>
      <div className="storyboard">
        <h2>{currentStoryboard.title}</h2>
        <p>{currentStoryboard.text}</p>
        <ul>
          {Object.keys(currentStoryboard.choices).map((choice, i) => (
            <li key={i}>
              <button onClick={() => handleChoiceSelect(choice)}>
                {currentStoryboard.choices[choice].text}
              </button>
            </li>
          ))}
        </ul>
      </div>
      <div className="save-load">
        <button onClick={handleSave}>Save Storyboard</button>
        <ul>
          {storyboards.map((storyboard, i) => (
            <li key={i}>
              <button onClick={() => handleLoad(storyboard)}>
                Load Storyboard {i+1}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
